import React from "react";
import Card from "react-bootstrap/Card";
import Row from "react-bootstrap/Row";
import Col from "react-bootstrap/Col";
import LinesEllipsis from "react-lines-ellipsis";
import Image from "react-bootstrap/Image";

class NewsCard extends React.Component {
    constructor(props) {
        super(props);
        this.state = {error: null, isLoaded: false, data: []};
    }

    callAPI(url) {
        fetch(url)
            .then(res => res.json())
            .then(
                res => {
                    this.setState({isLoaded: true, data: res['response']['results']});
                }, error => {
                    this.setState({isLoaded: true, error});
                })
    }


    componentDidMount() {
        this.callAPI("http://localhost:9000/guardian");
    }

    render() {
        var imgUrl = this.state.data[0]['blocks']['main']['elements']['0']['assets'].slice(-1)[0]['file'];
        if (this.state.error) {
            return <div>Error: {this.state.error.message}</div>;
        } else if (!this.state.isLoaded) {
            return <div>Loading...</div>
        } else {
            return (
                <Card style={{width: "100%"}}>
                    <div class="container-fluid">
                        <Row>
                            <Col md={3}>
                                <div className="align-items-center justify-content-center"
                                     style={{display: "flex", width: "100%", height: "100%"}}>
                                    <div className="img-thumbnail card-img"
                                         style={{backgroundImage: 'url(' + {imgUrl} + ')'}}></div>
                                </div>
                            </Col>
                            <Col md={9}>
                                <Card.Body>
                                    <Card.Title><strong><em>{this.state.data[0]['webTitle']}</em></strong></Card.Title>
                                    <Card.Text>
                                        <LinesEllipsis
                                            text={this.state.data[0]['blocks']['body']['0']['bodyTextSummary']}
                                            maxLine='3' ellipsis='...' basedOn='words'/>
                                    </Card.Text>
                                    <Card.Text><em>{this.state.data[0]['webPublicationDate'].slice(0, 10)}</em></Card.Text>
                                </Card.Body>
                            </Col>
                        </Row>
                    </div>
                </Card>
            );
        }
    }
}

export default NewsCard;
