import React from "react";
import Navbar from "react-bootstrap/Navbar";
import Nav from "react-bootstrap/Nav";
import 'bootstrap/dist/css/bootstrap.min.css';
import SelectBar from "../Components/SelectBar";
import {FaRegBookmark} from "react-icons/all";
import NewsCard from "../Components/NewsCard";
import Row from "react-bootstrap/Row";
import ReactSwitch from "react-switch";
import {Link} from "react-router-dom";

class Sports extends React.Component {
    constructor(props) {
        super(props);
        this.state = {SwitchChecked: true, checkedIcon: false, uncheckedIcon: false, onColor: '#4169E1', data: []};
        this.handleChange = this.handleChange.bind(this);
        if (window.localStorage.getItem("switchchecked")) {
            this.setState({SwitchChecked: window.localStorage.getItem("switchchecked")});
        }
    }

    handleChange = checked => {
        console.log(window.localStorage.getItem("switchchecked"));
        window.localStorage.setItem("switchchecked", checked);
        this.setState({SwitchChecked: checked});
        this.setState({isLoaded: true});
        if (checked) {
            this.callGuardian("sports");
        } else {
            this.callNYTimes("sports");
        }
    }

    callGuardian(s) {
        fetch("http://localhost:9000/guardian/" + s)
            .then(res => res.json())
            .then(
                res => {
                    this.setState({data: res['response']['results']});
                }, error => {
                    this.setState({error});
                })

    }

    callNYTimes(s) {
        fetch("http://localhost:9000/nytimes/" + s)
            .then(res => res.json())
            .then(
                res => {
                    this.setState({data: res['results']});
                }, error => {
                    this.setState({error});
                })

    }

    render() {
        return (
            <div>
                <Navbar collapseOnSelect expand="lg" bg="dark" variant="dark" style={{paddingLeft: '0'}}>
                    <SelectBar/>
                    <Navbar.Toggle aria-controls="responsive-navbar"/>
                    <Navbar.Collapse id="responsive-narbar">
                        <Nav className="mr-auto">
                            <Nav.Link as={Link} to="/">Home</Nav.Link>
                            <Nav.Link as={Link} to="/World">World</Nav.Link>
                            <Nav.Link as={Link} to="/Politics">Politics</Nav.Link>
                            <Nav.Link as={Link} to="/Business">Business</Nav.Link>
                            <Nav.Link as={Link} to="/Technology">Technology</Nav.Link>
                            <Nav.Link as={Link} to="/Sports">Sports</Nav.Link>
                        </Nav>
                    </Navbar.Collapse>
                    <FaRegBookmark color="white" className="mr-2"/>
                    <Navbar.Text style={{color: 'white'}} className="mr-2">NYTimes</Navbar.Text>
                    <ReactSwitch
                        onChange={this.handleChange}
                        checked={this.state.SwitchChecked}
                        checkedIcon={this.state.checkedIcon}
                        uncheckedIcon={this.state.uncheckedIcon}
                        onColor={this.state.onColor}
                        className="mr-2"/>
                    <Navbar.Text style={{color: 'white'}}>Guardian</Navbar.Text>
                </Navbar>
                <div className="container-fluid">
                    <Row>
                        <NewsCard checked={this.state.SwitchChecked} section="sports" data={this.state.data}/>
                    </Row>
                </div>
            </div>
        );
    }
}

export default Sports;
