import React from "react";

class DetailFavorite extends React.Component {
    constructor(props) {
        super(props);
        let checking = 'id' in props.data;
        this.state = {
            data: props.data,
            GuardianChecked: checking,
        };
    }

    render() {
        let title;
        let image;
        let date;
        let url;
        let id;
        let section;
        let sessionClassName;
        if (this.state.GuardianChecked) {
            title = this.state.data['webTitle'];
            date = this.state.data['webPublicationDate'].slice(0, 10);
            url = this.state.data['webUrl'];
            id = this.state.data['id'];
            section = this.state.data[i]['sectionId'].toUpperCase();
            if (section[i] === 'SPORT')
                section[i] = 'SPORTS';
            if (section[i] === 'WORLD')
                sectionClassName[i] = 'badge-world';
            else if (section[i] === 'POLITICS')
                sectionClassName[i] = 'badge-politics';
            else if (section[i] === 'BUSINESS')
                sectionClassName[i] = 'badge-business';
            else if (section[i] === 'TECHNOLOGY')
                sectionClassName[i] = 'badge-technology';
            else if (section[i] === 'SPORTS')
                sectionClassName[i] = 'badge-sports';
            else
                sectionClassName[i] = 'badge-other';

        } else {
            title[i] = this.state.data[i]['title'];
            description[i] = this.state.data[i]['abstract'];
            date[i] = this.state.data[i]['published_date'].slice(0, 10);
            section[i] = this.state.data[i]['section'].toUpperCase();
            url[i] = this.state.data[i]['url'];
            if ('multimedia' in this.state.data[i] && this.state.data[i]['multimedia'] !== null && this.state.data[i]['multimedia'].length !== 0)
                image[i] = this.state.data[i]['multimedia']['0']['url'];
            else
                image[i] = 'https://upload.wikimedia.org/wikipedia/commons/0/0e/Nytimes_hq.jpg';
            if (section[i] === 'WORLD')
                sectionClassName[i] = 'badge-world';
            else if (section[i] === 'POLITICS')
                sectionClassName[i] = 'badge-politics';
            else if (section[i] === 'BUSINESS')
                sectionClassName[i] = 'badge-business';
            else if (section[i] === 'TECHNOLOGY')
                sectionClassName[i] = 'badge-technology';
            else if (section[i] === 'SPORTS')
                sectionClassName[i] = 'badge-sports';
            else
                sectionClassName[i] = 'badge-other';

        }
        return (
            <div>

            </div>
        );
    }
}

export default DetailFavorite;
