import React from "react";
import Card from "react-bootstrap/Card";
import Row from "react-bootstrap/Row";
import Col from "react-bootstrap/Col";
import Image from "react-bootstrap/Image";
import LoadSpinner from "./LoadSpinner";
import Share from "./Share";
import Badge from "react-bootstrap/Badge";
import {withRouter} from "react-router-dom";

class SearchResult extends React.Component {
    constructor(props) {
        super(props);
        this.state = {error: null, isLoaded: false, data: [], GuardianChecked: props.checked, q: props.q};
    }

    callGuardian(q) {
        fetch("http://localhost:9000/guardian/search" + q)
            .then(res => res.json())
            .then(
                res => {
                    this.setState({isLoaded: true, data: res['response']['results']});
                }, error => {
                    this.setState({isLoaded: true, error});
                })
    }

    callNYTimes(q) {
        fetch("http://localhost:9000/nytimes/search" + q)
            .then(res => res.json())
            .then(
                res => {
                    this.setState({isLoaded: true, data: res['response']['docs']});
                }, error => {
                    this.setState({isLoaded: true, error});
                })
    }

    componentDidMount() {
        console.log(this.state);
        if (this.state.GuardianChecked)
            this.callGuardian(this.state.q);
        else
            this.callNYTimes(this.state.q);
    }

    clickCard = e => {
        let clickTarget = e.target.localName;
        if (clickTarget === "path" || clickTarget === "span" || clickTarget === "circle" || clickTarget === "svg")
            return;
        let id;
        let i = e.currentTarget.id[5];
        if (this.state.GuardianChecked)
            id = this.state.data[i]['id'];
        else
            id = this.state.data[i]['url'];
        let queryString = "id=" + id;
        this.props.history.push('/article?' + queryString);
    };

    render() {
        if (this.state.error) {
            return <div>Error: {this.state.error.message}</div>;
        } else if (!this.state.isLoaded) {
            return <LoadSpinner/>;
        } else {
            let title = new Array(10);
            let image = new Array(10);
            let description = new Array(10);
            let date = new Array(10);
            let section = new Array(10);
            let sectionClassName = new Array(10);
            let url = new Array(10);
            if (this.state.GuardianChecked) {
                for (let i = 0; i < 10; i++) {
                    if ('main' in this.state.data[i]['blocks'] && this.state.data[i]['blocks']['main']['elements']['0']['assets'].length !== 0)
                        image[i] = this.state.data[i]['blocks']['main']['elements']['0']['assets'].slice(-1)[0]['file'];
                    else
                        image[i] = 'https://assets.guim.co.uk/images/eada8aa27c12fe2d5afa3a89d3fbae0d/fallback-logo.png';
                    title[i] = this.state.data[i]['webTitle'];
                    description[i] = this.state.data[i]['blocks']['body']['0']['bodyTextSummary'];
                    date[i] = this.state.data[i]['webPublicationDate'].slice(0, 10);
                    url[i] = this.state.data[i]['webUrl'];
                    section[i] = this.state.data[i]['sectionId'].toUpperCase();
                    if (section[i] === 'SPORT')
                        section[i] = 'SPORTS';
                    if (section[i] === 'WORLD')
                        sectionClassName[i] = 'badge-world';
                    else if (section[i] === 'POLITICS')
                        sectionClassName[i] = 'badge-politics';
                    else if (section[i] === 'BUSINESS')
                        sectionClassName[i] = 'badge-business';
                    else if (section[i] === 'TECHNOLOGY')
                        sectionClassName[i] = 'badge-technology';
                    else if (section[i] === 'SPORTS')
                        sectionClassName[i] = 'badge-sports';
                    else
                        sectionClassName[i] = 'badge-other';
                }
            } else {
                for (let i = 0; i < 10; i++) {
                    title[i] = this.state.data[i]['headline']['main'];
                    description[i] = this.state.data[i]['abstract'];
                    date[i] = this.state.data[i]['pub_date'].slice(0, 10);
                    section[i] = this.state.data[i]['news_desk'].toUpperCase();
                    url[i] = this.state.data[i]['web_url'];
                    if ('multimedia' in this.state.data[i] && this.state.data[i]['multimedia'] !== null && this.state.data[i]['multimedia'].length !== 0) {
                        let k = 0;
                        for (; k < this.state.data[i]['multimedia'].length; k++) {
                            if (this.state.data[i]['multimedia'][k]['width'] >= 2000)
                                break;
                        }
                        if (k < this.state.data[i]['multimedia'].length) {
                            let tmpUrl = this.state.data[i]['multimedia'][k]['url'];
                            if (tmpUrl[0] === 'i')
                                image[i] = 'https://www.nytimes.com/' + tmpUrl;
                            else
                                image[i] = tmpUrl;
                        } else
                            image[i] = 'https://upload.wikimedia.org/wikipedia/commons/0/0e/Nytimes_hq.jpg';
                    } else
                        image[i] = 'https://upload.wikimedia.org/wikipedia/commons/0/0e/Nytimes_hq.jpg';
                    if (section[i] === 'WORLD')
                        sectionClassName[i] = 'badge-world';
                    else if (section[i] === 'POLITICS')
                        sectionClassName[i] = 'badge-politics';
                    else if (section[i] === 'BUSINESS')
                        sectionClassName[i] = 'badge-business';
                    else if (section[i] === 'TECHNOLOGY')
                        sectionClassName[i] = 'badge-technology';
                    else if (section[i] === 'SPORTS')
                        sectionClassName[i] = 'badge-sports';
                    else
                        sectionClassName[i] = 'badge-other';
                }
            }
            return (
                <div style={{boxSizing: "content-box"}}>
                    <Col md={3}>
                        <Card id="card-0" onClick={this.clickCard}>
                            <div className="container-fluid">
                                <div className="align-items-center justify-content-center"
                                     style={{
                                         display: "flex",
                                         width: "100%",
                                         height: "100%",
                                         padding: "1.25rem"
                                     }}>
                                    <Image className="image-thumbnail img-fluid" src={image[0]}/>
                                </div>
                                <Card.Body>
                                    <Card.Title>
                                        <strong><em>{title[0]}</em></strong>
                                        <Share url={url[0]} title={title[0]}/>
                                    </Card.Title>
                                    <Card.Text className="description">
                                        {description[0]}
                                    </Card.Text>
                                    <Card.Text>
                                        <em>{date[0]}</em>
                                        <Badge className={sectionClassName[0]}>{section[0]}</Badge>
                                    </Card.Text>
                                </Card.Body>
                            </div>
                        </Card>
                    </Col>
                    <Col md={3}>
                        <Card id="card-1" onClick={this.clickCard}>
                            <div class="container-fluid">
                                <Row>
                                    <Col md={3}>
                                        <div className="align-items-center justify-content-center"
                                             style={{
                                                 display: "flex",
                                                 width: "100%",
                                                 height: "100%",
                                                 padding: "1.25rem"
                                             }}>
