import React from "react";
import Navbar from "react-bootstrap/Navbar";
import Nav from "react-bootstrap/Nav";
import 'bootstrap/dist/css/bootstrap.min.css';
import SelectBar from "../Components/SelectBar";
import {FaRegBookmark} from "react-icons/all";
import Row from "react-bootstrap/Row";
import ReactSwitch from "react-switch";
import {Link, NavLink} from 'react-router-dom';
import ReactTooltip from "react-tooltip";
import CardList from "../Components/CardList";

class World extends React.Component {
    constructor(props) {
        super(props);
        let checking;
        if (window.localStorage.getItem("switchchecked")) {
            checking = (window.localStorage.getItem("switchchecked") === "true");
        } else {
            checking = true;
        }
        this.state = {
            SwitchChecked: checking,
            checkedIcon: false,
            uncheckedIcon: false,
            onColor: '#49f',
            offColor: '#eee',
            data: []
        };
        this.handleChange = this.handleChange.bind(this);
    }

    handleChange = checked => {
        window.localStorage.setItem("switchchecked", checked);
        this.setState({SwitchChecked: checked});
        this.setState({isLoaded: true});
        if (checked) {
            this.callGuardian("world");
        } else {
            this.callNYTimes("world");
        }
    };

    callGuardian(s) {
        fetch("https://yli59322-hw8-571-backend.appspot.com/guardian/" + s)
            .then(res => res.json())
            .then(
                res => {
                    this.setState({data: res});
                }, error => {
                    this.setState({error});
                })

    }

    callNYTimes(s) {
        fetch("https://yli59322-hw8-571-backend.appspot.com/nytimes/" + s)
            .then(res => res.json())
            .then(
                res => {
                    this.setState({data: res});
                }, error => {
                    this.setState({error});
                })

    }

    render() {
        return (
            <div className="container-fluid">
                <Row>
                    <CardList checked={this.state.SwitchChecked} section="world" data={this.state.data}/>
                </Row>
            </div>
        );
    }
}

export default World;
