import React from "react";
import Select from "react-select";
import AsyncSelect from "react-select/lib/Async";

class SelectBar extends React.Component {
    constructor(props) {
        super(props);
        this.state = {inputValue: ""};
    }

    autosuggest = () => {
        fetch("https://yutong-li.cognitiveservices.azure.com/bing/v7.0/suggestions?mkt=en-US&q=" + this.state.inputValue, {
            headers: {
                'Ocp-Apim-Subscription-Key': 'a42b6d1b1fad47d7b33425d7eea2f04f'
            }
        }).then(res => res.json())
            .then(res => {
                let suggestions = [];
                console.log(res);
                if ("suggestionGroups" in res) {
                    var resfilter = res["suggestionGroups"][0]["searchSuggestions"];
                    for (var i = 0; i < resfilter.length; i++) {
                        suggestions.push(resfilter[i]["displayText"]);
                    }
                    this.setState({suggest: suggestions});
                }
                console.log(suggestions);
                //return suggestions;
            })
    };

    testfunc = () => {
        let testarr = ['a', 'ab', 'abc'];
    };

    loadOptions = (inputValue, callback) => {
        setTimeout(() => {
            callback(this.autosuggest());
        }, 1000);
    };

    handleInputChange = (newValue) => {
        const inputValue = newValue.replace(/\W/g, '');
        this.setState({inputValue});
        this.autosuggest();
        return inputValue;
    };

    render() {
        return (
            <AsyncSelect placeholder="Enter keyword .." className="selectBar" onInputChange={this.handleInputChange}
                         loadOptions={this.loadOptions}/>
        );
    }
}

export default SelectBar;
