import React from "react";
import Card from "react-bootstrap/Card";
import Row from "react-bootstrap/Row";
import Col from "react-bootstrap/Col";
import Image from "react-bootstrap/Image";
import Badge from "react-bootstrap/Badge";
import Share from "./Share";
import LoadSpinner from "./LoadSpinner";

class NewsCard extends React.Component {
    constructor(props) {
        super(props);
        this.state = {error: null, isLoaded: false, data: [], GuardianChecked: props.checked, section: props.section};
        console.log(props);
        this.card1 = React.createRef();
        this.clickCard = this.clickCard.bind(this);
    }

    componentWillReceiveProps(nextProps) {
        if (nextProps.GuardianChecked)
            this.callGuardian(this.state.section);
        else
            this.callNYTimes(this.state.section);
        this.setState({GuardianChecked: nextProps.checked}, () => {
            console.log(this.state);
        });
    }

    componentWillUpdate(nextProps, nextState){
        if (this.state.GuardianChecked)
            this.callGuardian(this.state.section);
        else
            this.callNYTimes(this.state.section);
    }

    callGuardian(s) {
        if (s === "") {
            fetch("http://localhost:9000/guardian")
                .then(res => res.json())
                .then(
                    res => {
                        this.setState({isLoaded: true, data: res['response']['results']});
                    }, error => {
                        this.setState({isLoaded: true, error});
                    })
        } else {
            fetch("http://localhost:9000/guardian/" + s)
                .then(res => res.json())
                .then(
                    res => {
                        this.setState({isLoaded: true, data: res['response']['results']});
                    }, error => {
                        this.setState({isLoaded: true, error});
                    })
        }
    }

    callNYTimes(s) {
        if (s === "") {
            fetch("http://localhost:9000/nytimes")
                .then(res => res.json())
                .then(
                    res => {
                        this.setState({isLoaded: true, data: res['results']});
                    }, error => {
                        this.setState({isLoaded: true, error});
                    })
        } else {
            fetch("http://localhost:9000/nytimes/" + s)
                .then(res => res.json())
                .then(
                    res => {
                        this.setState({isLoaded: true, data: res['results']});
                    }, error => {
                        this.setState({isLoaded: true, error});
                    })
        }
    }

    componentDidMount() {
        console.log(this.state);
        if (this.state.GuardianChecked)
            this.callGuardian(this.state.section);
        else
            this.callNYTimes(this.state.section);
    }

    clickCard() {
        if (this.state.isLoaded) {
            //this.card1.current.style.display = "none";
        }
    }

    render() {
        if (this.state.error) {
            return <div>Error: {this.state.error.message}</div>;
        } else if (!this.state.isLoaded) {
            return <LoadSpinner/>;
        } else {
            let title = new Array(10);
            let image = new Array(10);
            let description = new Array(10);
            let date = new Array(10);
            let section = new Array(10);
            let sectionClassName = new Array(10);
            let url = new Array(10);
            if (this.state.GuardianChecked) {
                for (let i = 0; i < 10; i++) {
                    if ('main' in this.state.data[i]['blocks'] && this.state.data[i]['blocks']['main']['elements']['0']['assets'].length !== 0)
                        image[i] = this.state.data[i]['blocks']['main']['elements']['0']['assets'].slice(-1)[0]['file'];
                    else
                        image[i] = 'https://assets.guim.co.uk/images/eada8aa27c12fe2d5afa3a89d3fbae0d/fallback-logo.png';
                    title[i] = this.state.data[i]['webTitle'];
                    description[i] = this.state.data[i]['blocks']['body']['0']['bodyTextSummary'];
                    date[i] = this.state.data[i]['webPublicationDate'].slice(0, 10);
                    url[i] = this.state.data[i]['webUrl'];
                    section[i] = this.state.data[i]['sectionId'].toUpperCase();
                    if (section[i] === 'SPORT')
                        section[i] = 'SPORTS';
                    if (section[i] === 'WORLD')
                        sectionClassName[i] = 'badge-world';
                    else if (section[i] === 'POLITICS')
                        sectionClassName[i] = 'badge-politics';
                    else if (section[i] === 'BUSINESS')
                        sectionClassName[i] = 'badge-business';
                    else if (section[i] === 'TECHNOLOGY')
                        sectionClassName[i] = 'badge-technology';
                    else if (section[i] === 'SPORTS')
                        sectionClassName[i] = 'badge-sports';
                    else
                        sectionClassName[i] = 'badge-other';
                }
            } else {
                for (let i = 0; i < 10; i++) {
                    title[i] = this.state.data[i]['title'];
                    description[i] = this.state.data[i]['abstract'];
                    date[i] = this.state.data[i]['published_date'].slice(0, 10);
                    section[i] = this.state.data[i]['section'].toUpperCase();
                    url[i] = this.state.data[i]['url'];
                    if ('multimedia' in this.state.data[i] && this.state.data[i]['multimedia'] !== null && this.state.data[i]['multimedia'].size !== 0)
                        image[i] = this.state.data[i]['multimedia']['0']['url'];
                    else
                        image[i] = 'https://upload.wikimedia.org/wikipedia/commons/0/0e/Nytimes_hq.jpg';
                    if (section[i] === 'WORLD')
                        sectionClassName[i] = 'badge-world';
                    else if (section[i] === 'POLITICS')
                        sectionClassName[i] = 'badge-politics';
                    else if (section[i] === 'BUSINESS')
                        sectionClassName[i] = 'badge-business';
                    else if (section[i] === 'TECHNOLOGY')
                        sectionClassName[i] = 'badge-technology';
                    else if (section[i] === 'SPORTS')
                        sectionClassName[i] = 'badge-sports';
                    else
                        sectionClassName[i] = 'badge-other';
                }
            }
            return (
                <div style={{boxSizing: "content-box"}}>
                    <Card ref={this.card1} onClick={this.clickCard}>
                        <div class="container-fluid">
                            <Row>
                                <Col md={3}>
                                    <div className="align-items-center justify-content-center"
                                         style={{display: "flex", width: "100%", height: "100%", padding: "1.25rem"}}>
                                        <Image className="image-thumbnail img-fluid" src={image[0]}/>
                                    </div>
                                </Col>
                                <Col md={9}>
                                    <Card.Body>
                                        <Card.Title>
                                            <strong><em>{title[0]}</em></strong>
                                            <Share url={url[0]} title={title[0]}/>
                                        </Card.Title>
                                        <Card.Text className="description">
                                            {description[0]}
                                        </Card.Text>
                                        <Card.Text>
                                            <em>{date[0]}</em>
                                            <Badge className={sectionClassName[0]}>{section[0]}</Badge>
                                        </Card.Text>
                                    </Card.Body>
                                </Col>
                            </Row>
                        </div>
