import React from "react";
import Card from "react-bootstrap/Card";
import Row from "react-bootstrap/Row";
import Col from "react-bootstrap/Col";
import Image from "react-bootstrap/Image";
import Badge from "react-bootstrap/Badge";
import Share from "./Share";
import LoadSpinner from "./LoadSpinner";

class DetailArticle extends React.Component {
    constructor(props) {
        super(props);
        this.state = {error: null, isLoaded: false, data: [], GuardianChecked: props.checked, id: props.id};
        console.log(props);
    }

    callGuardian(id) {
        fetch("http://localhost:9000/guardian/article" + id)
            .then(res => res.json())
            .then(
                res => {
                    this.setState({isLoaded: true, data: res['response']['content']});
                }, error => {
                    this.setState({isLoaded: true, error});
                })
    }

    callNYTimes(id) {
        fetch("http://localhost:9000/nytimes/article" + id)
            .then(res => res.json())
            .then(
                res => {
                    this.setState({isLoaded: true, data: res['results']});
                }, error => {
                    this.setState({isLoaded: true, error});
                })
    }

    componentDidMount() {
        console.log(this.state);
        if (this.state.GuardianChecked)
            this.callGuardian(this.state.id);
        else
            this.callNYTimes(this.state.id);
    }

    render() {
        if (this.state.error) {
            return <div>Error: {this.state.error.message}</div>;
        } else if (!this.state.isLoaded) {
            return <LoadSpinner/>;
        } else {
            let title;
            let image;
            let date;
            let url;
            let description;
            if (this.state.GuardianChecked) {
                title = this.state.data['webTitle'];
                date = this.state.data['webPublicationDate'].slice(0, 10);
            } else {

            }
            return (
                <div style={{boxSizing: "content-box"}}>
                    <Card>
                        <div className="container-fluid">
                            <Row>
                                <Col md={3}>
                                    <div className="align-items-center justify-content-center"
                                         style={{display: "flex", width: "100%", height: "100%", padding: "1.25rem"}}>
                                        <Image className="image-thumbnail img-fluid" src={image}/>
                                    </div>
                                </Col>
                                <Col md={9}>
                                    <Card.Body>
                                        <Card.Title>
                                            <strong><em>{title[0]}</em></strong>
                                            <Share url={url[0]} title={title}/>
                                        </Card.Title>
                                        <Card.Text className="description">
                                            {description[0]}
                                        </Card.Text>
                                    </Card.Body>
                                </Col>
                            </Row>
                        </div>
                    </Card>
                </div>
            );
        }
    }
}

export default DetailArticle;
