import React from "react";
import Card from "react-bootstrap/Card";
import Container from "react-bootstrap/Container";
import Row from "react-bootstrap/Row";
import Col from "react-bootstrap/Col";
import LinesEllipsis from "react-lines-ellipsis";
import Image from "react-bootstrap/Image";

class NewsCard extends React.Component {
    constructor(props) {
        super(props);
        this.state = {error: null, isLoaded: false, data: []};
    }

    callAPI(url) {
        fetch(url)
            .then(res => res.json())
            .then(
                res => {
                    this.setState({isLoaded: true, data: res['response']['results']});
                }, error => {
                    this.setState({isLoaded: true, error});
                })
    }


    componentDidMount() {
        this.callAPI("http://localhost:9000/guardian");
    }

    render() {
        if (this.state.error) {
            return <div>Error: {this.state.error.message}</div>;
        } else if (!this.state.isLoaded) {
            return <div>Loading...</div>
        } else {
            return (
                <Card style={{width: '100%'}}>
                    <div class="container-fluid">
                        <Row>
                            <Col md={4} className="align-self-center">
                                <Image variant="left" className="img-thumbnail" src={this.state.data[0]['blocks']['main']['elements']['0']['assets'].slice(-1)[0]['file']} style={{width: '300px'}}/>
                            </Col>
                            <Col md={8}>
                                <Card.Body>
                                    <Card.Title>{this.state.data[0]['webTitle']}</Card.Title>
                                    <Card.Text>
                                        <LinesEllipsis text={this.state.data[0]['blocks']['body']['0']['bodyTextSummary']}
                                           maxLine='3' ellipsis='...' basedOn='words'/>
                                    </Card.Text>
                                    <Card.Text>{this.state.data[0]['webPublicationDate'].slice(0, 10)}</Card.Text>
                                </Card.Body>
                            </Col>
                        </Row>
                    </div>
                </Card>
            );
        }
    }
}

export default NewsCard;
