import React from 'react';
import logo from './logo.svg';
import './App.css';
import Navbar from "react-bootstrap/Navbar";
import Form from "react-bootstrap/Form";
import FormControl from "react-bootstrap/FormControl";
import Nav from "react-bootstrap/Nav";
import 'bootstrap/dist/css/bootstrap.min.css';
import SelectBar from "./Components/SelectBar";
import SwitchBar from "./Components/SwitchBar";

class App extends React.Component {
    constructor(props) {
        super(props);
        this.state = {apiResponse: ""};
    }

    callAPI() {
        fetch("http://localhost:9000/test")
            .then(res => res.text())
            .then(res => this.setState({apiResponse: res}))
    }

    componentWillMount() {
        this.callAPI();
    }

    render() {
        return (
            <div>
                <Navbar bg="dark" variant="dark">
                    <SelectBar />
                    <Nav className="mr-auto">
                        <Nav.Link href="#">Home</Nav.Link>
                        <Nav.Link href="#">World</Nav.Link>
                        <Nav.Link href="#">Politics</Nav.Link>
                        <Nav.Link href="#">Business</Nav.Link>
                        <Nav.Link href="#">Technology</Nav.Link>
                        <Nav.Link href="#">Sports</Nav.Link>
                    </Nav>
                    <Nav.Item class="outline-light">NYTimes</Nav.Item>
                    <SwitchBar/>
                    <Nav.Item>Guardian</Nav.Item>
                </Navbar>
            </div>
        );
    }
}

export default App;
