import React from "react";
import Navbar from "react-bootstrap/Navbar";
import Nav from "react-bootstrap/Nav";
import 'bootstrap/dist/css/bootstrap.min.css';
import SelectBar from "../Components/SelectBar";
import {FaRegBookmark} from "react-icons/all";
import CardList from "../Components/CardList";
import Row from "react-bootstrap/Row";
import ReactSwitch from "react-switch";
import {Link} from "react-router-dom";
import ReactTooltip from "react-tooltip";

class Sports extends React.Component {
    constructor(props) {
        super(props);
        let checking;
        if (window.localStorage.getItem("switchchecked")) {
            checking = (window.localStorage.getItem("switchchecked") === "true");
        } else {
            checking = true;
        }
        this.state = {
            SwitchChecked: checking,
            checkedIcon: false,
            uncheckedIcon: false,
            onColor: '#49f',
            offColor: '#eee',
            data: []
        };
        this.handleChange = this.handleChange.bind(this);
    }

    handleChange = checked => {
        window.localStorage.setItem("switchchecked", checked);
        this.setState({SwitchChecked: checked});
        this.setState({isLoaded: true});
        if (checked) {
            this.callGuardian("politics");
        } else {
            this.callNYTimes("politics");
        }
    };

    callGuardian(s) {
        fetch("https://yli59322-hw8-571-backend.appspot.com/guardian/" + s)
            .then(res => res.json())
            .then(
                res => {
                    this.setState({data: res});
                }, error => {
                    this.setState({error});
                })

    }

    callNYTimes(s) {
        fetch("https://yli59322-hw8-571-backend.appspot.com/nytimes/" + s)
            .then(res => res.json())
            .then(
                res => {
                    this.setState({data: res});
                }, error => {
                    this.setState({error});
                })

    }

    render() {
        return (
            <div>
                <Navbar className="Navbar" collapseOnSelect expand="lg" variant="dark">
                    <SelectBar/>
                    <Navbar.Toggle aria-controls="responsive-navbar"/>
                    <Navbar.Collapse id="responsive-narbar">
                        <Nav className="mr-auto">
                            <Nav.Link as={Link} to="/">Home</Nav.Link>
                            <Nav.Link as={Link} to="/World">World</Nav.Link>
                            <Nav.Link as={Link} to="/Politics">Politics</Nav.Link>
                            <Nav.Link as={Link} to="/Business">Business</Nav.Link>
                            <Nav.Link as={Link} to="/Technology">Technology</Nav.Link>
                            <Nav.Link as={Link} to="/Sports" style={{"color": "white"}}>Sports</Nav.Link>
                        </Nav>
                        <p
                            data-tip="Bookmark"
                            data-for="BookmarkTT"
                            data-offset="{'left' : -5, 'right' : -5}"
                            className="bookmarkbar"
                        >
                            <Link to="/favorites">
                                <FaRegBookmark color="white" className="mr-2" style={{"cursor": "default"}}/>
                            </Link>
                        </p>
                        <ReactTooltip
                            id="BookmarkTT"
                            place="bottom"
                            effect="solid"
                            type="dark"/>
                        <Nav style={{color: 'white', padding: 8}} className="mr-2">NYTimes</Nav>
                        <ReactSwitch
                            onChange={this.handleChange}
                            checked={this.state.SwitchChecked}
                            checkedIcon={this.state.checkedIcon}
                            uncheckedIcon={this.state.uncheckedIcon}
                            onColor={this.state.onColor}
                            offColor={this.state.offColor}
                            className="mr-2 switchbar"
                        />
                        <Navbar style={{color: 'white', padding: 8}}>Guardian</Navbar>
                    </Navbar.Collapse>
                </Navbar>
                <div className="container-fluid">
                    <Row>
                        <CardList checked={this.state.SwitchChecked} section="sports" data={this.state.data}/>
                    </Row>
                </div>
            </div>
        );
    }
}

export default Sports;
