import React from "react";
import Navbar from "react-bootstrap/Navbar";
import Nav from "react-bootstrap/Nav";
import 'bootstrap/dist/css/bootstrap.min.css';
import SelectBar from "../Components/SelectBar";
import {FaRegBookmark} from "react-icons/all";
import Row from "react-bootstrap/Row";
import {Link} from 'react-router-dom';
import SearchResult from "../Components/SearchResult";
import ReactTooltip from "react-tooltip";

class Search extends React.Component {
    constructor(props) {
        super(props);
    }

    render() {
        let checking;
        if (window.localStorage.getItem("switchchecked")) {
            checking = (window.localStorage.getItem("switchchecked") === "true");
        } else {
            checking = true;
        }
        return (
            <div>
                <Navbar collapseOnSelect expand="lg" variant="dark">
                    <SelectBar/>
                    <Navbar.Toggle aria-controls="responsive-navbar"/>
                    <Navbar.Collapse id="responsive-narbar">
                        <Nav className="mr-auto">
                            <Nav.Link as={Link} to="/">Home</Nav.Link>
                            <Nav.Link as={Link} to="/World">World</Nav.Link>
                            <Nav.Link as={Link} to="/Politics">Politics</Nav.Link>
                            <Nav.Link as={Link} to="/Business">Business</Nav.Link>
                            <Nav.Link as={Link} to="/Technology">Technology</Nav.Link>
                            <Nav.Link as={Link} to="/Sports">Sports</Nav.Link>
                        </Nav>
                    </Navbar.Collapse>
                    <p data-tip="Bookmark" data-for="BookmarkTT" data-offset="{'left' : -5, 'right' : -5}"
                       style={{"margin-bottom": 0}}>
                        <Link to="/favorites">
                            <FaRegBookmark color="white" className="mr-2" style={{"cursor": "default"}}/>
                        </Link>
                    </p>
                    <ReactTooltip
                        id="BookmarkTT"
                        place="bottom"
                        effect="solid"
                        type="dark"/>
                </Navbar>
                <div className="container-fluid">
                    <h4 style={{"margin-left": "0.25rem", "margin-bottom": 0}}>Results</h4>
                    <SearchResult checked={checking} q={window.location.search}/>
                </div>
            </div>
        );
    }
}

export default Search;
