import React from "react";
import Navbar from "react-bootstrap/Navbar";
import Nav from "react-bootstrap/Nav";
import 'bootstrap/dist/css/bootstrap.min.css';
import SelectBar from "../Components/SelectBar";
import {FaRegBookmark} from "react-icons/all";
import Row from "react-bootstrap/Row";
import {Link} from 'react-router-dom';
import DetailArticle from "../Components/DetailArticle";
import Comment from "../Components/Comment";
import ReactTooltip from "react-tooltip";
import ReactSwitch from "react-switch";

class Article extends React.Component {
    constructor(props) {
        super(props);
        let checking = window.location.search.substr(4, 4) !== 'http';
        this.state = {
            error: null,
            SwitchChecked: checking
        };
    }

    render() {
        return (
            <div>
                <Navbar className="Navbar" collapseOnSelect expand="lg" variant="dark">
                    <SelectBar/>
                    <Navbar.Toggle aria-controls="responsive-navbar"/>
                    <Navbar.Collapse id="responsive-narbar">
                        <Nav className="mr-auto">
                            <Nav.Link as={Link} to="/">Home</Nav.Link>
                            <Nav.Link as={Link} to="/World">World</Nav.Link>
                            <Nav.Link as={Link} to="/Politics">Politics</Nav.Link>
                            <Nav.Link as={Link} to="/Business">Business</Nav.Link>
                            <Nav.Link as={Link} to="/Technology">Technology</Nav.Link>
                            <Nav.Link as={Link} to="/Sports">Sports</Nav.Link>
                        </Nav>
                        <p
                            data-tip="Bookmark"
                            data-for="BookmarkTT"
                            data-offset="{'left' : -5, 'right' : -5}"
                            className="bookmarkbar"
                        >
                            <Link to="/favorites">
                                <FaRegBookmark color="white" className="mr-2" style={{"cursor": "default"}}/>
                            </Link>
                        </p>
                        <ReactTooltip
                            id="BookmarkTT"
                            place="bottom"
                            effect="solid"
                            type="dark"/>
                    </Navbar.Collapse>
                </Navbar>
                <div className="container-fluid">
                    <Row>
                        <DetailArticle id={window.location.search} checked={this.state.SwitchChecked}/>
                    </Row>
                    <Row>
                        <Comment id={window.location.search}/>
                    </Row>
                </div>
            </div>
        );
    }
}

export default Article;
