import React, {useState} from "react";
import {
    EmailShareButton,
    FacebookShareButton,
    TwitterShareButton,
    FacebookIcon,
    EmailIcon,
    TwitterIcon,
} from 'react-share';
import {MdShare} from "react-icons/all";
import Modal from "react-bootstrap/Modal";

function Share(props) {
    const [show, setShow] = useState(false);

    const handleShow = () => setShow(true);
    const handleClose = () => setShow(false);
    let hashtag = new Array[1];
    hashtag[0] = "#CSCI_571_NewsApp";
    return (
        <>
            <MdShare onClick={handleShow} className="share-icon"/>
            <Modal show={show} onHide={handleClose}>
                <Modal.Header closeButton>
                    <Modal.Title>{props.title}</Modal.Title>
                </Modal.Header>
                <Modal.Body>
                    Share via:<br/>
                    <FacebookShareButton url={props.url} hashtag={hashtag[0]}>
                        <FacebookIcon size={40} round/>
                    </FacebookShareButton>
                    <TwitterShareButton url={props.url} hashtags={hashtag}>
                        <TwitterIcon size={40} round/>
                    </TwitterShareButton>
                    <EmailShareButton url={props.url} subject={hashtag[0]}>
                        <EmailIcon size={40} round/>
                    </EmailShareButton>
                </Modal.Body>
            </Modal>
        </>
    );
};

export default Share;
