import React, {useState} from "react";
import {EmailShareButton, FacebookShareButton, TwitterShareButton,} from 'react-share';
import {MdShare} from "react-icons/all";
import Modal from "react-bootstrap/Modal";

class Share extends React.Component {
    constructor(props) {
        super(props);
        this.state = {url: props.url, title: props.title};
    }

    render() {
        const [show, setShow] = useState(false);
        const handleShow = () => setShow(true);
        const handleClose = () => setShow(false);
        return (
            <Modal show={show} onHide={handleClose}>
                <Modal.Header closeButton>
                    <Modal.Title>{this.state.title}</Modal.Title>
                </Modal.Header>
                <Modal.Body>
                    Share via:<br/>
                    <FacebookShareButton url={this.state.url} hashtag="#CSCI_571_NewsApp"/>
                    <TwitterShareButton url={this.state.url} hashtags="#CSCI_571_NewsApp"/>
                    <EmailShareButton url={this.state.url} subject="#CSCI_571_NewsApp"/>
                </Modal.Body>
            </Modal>

        );
    }
}

export default Share;
