import React, {Component} from "react";
import DetailCard from "./DetailCard";
import LoadSpinner from "./LoadSpinner";

class CardList extends Component {
    constructor(props) {
        super(props);
        this.state = {
            error: null,
            isLoaded: false,
            data: [],
            GuardianChecked: props.checked,
            section: props.section
        };
    }

    static getDerivedStateFromProps(nextProps, nextState) {
        if (nextProps.checked !== nextState.GuardianChecked) {
            return {
                GuardianChecked: nextProps.checked,
            };
        }
        return null;
    }

    callGuardian(s) {
        if (s === "") {
            fetch("http://localhost:9000/guardian")
                .then(res => res.json())
                .then(
                    res => {
                        this.setState({isLoaded: true, data: res['response']['results']});
                    }, error => {
                        this.setState({isLoaded: true, error});
                    })
        } else {
            fetch("http://localhost:9000/guardian/" + s)
                .then(res => res.json())
                .then(
                    res => {
                        this.setState({isLoaded: true, data: res['response']['results']});
                    }, error => {
                        this.setState({isLoaded: true, error});
                    })
        }
    }

    callNYTimes(s) {
        if (s === "") {
            fetch("http://localhost:9000/nytimes")
                .then(res => res.json())
                .then(
                    res => {
                        this.setState({isLoaded: true, data: res['results']});
                    }, error => {
                        this.setState({isLoaded: true, error});
                    })
        } else {
            fetch("http://localhost:9000/nytimes/" + s)
                .then(res => res.json())
                .then(
                    res => {
                        this.setState({isLoaded: true, data: res['results']});
                    }, error => {
                        this.setState({isLoaded: true, error});
                    })
        }
    }

    componentDidMount() {
        if (this.state.GuardianChecked)
            this.callGuardian(this.state.section);
        else
            this.callNYTimes(this.state.section);
    }

    render() {
        if (this.state.GuardianChecked)
            this.callGuardian(this.state.section);
        else
            this.callNYTimes(this.state.section);
        if (this.state.error) {
            return <div>Error: {this.state.error.message}</div>;
        } else if (!this.state.isLoaded) {
            return <LoadSpinner/>;
        } else {
            let array = [];
            for (let i = 0; i < this.state.data.length; i++) {
                array.push(
                    <DetailCard key={i} data={this.state.data[i]} checked={this.state.GuardianChecked}/>
                );
            }
            return (
                <div style={{boxSizing: "content-box"}}>
                    {array}
                </div>
            );
        }
    };
}

export default CardList;
