import React, {useState} from "react";
import {
    EmailShareButton,
    FacebookShareButton,
    TwitterShareButton,
    FacebookIcon,
    EmailIcon,
    TwitterIcon,
} from 'react-share';
import ReactTooltip from "react-tooltip";

class DetailShare extends React.Component {
    constructor(props) {
        super(props);
        this.state = {
            place: "top",
            type: "dark",
            effect: "solid",
        };
    }

    render() {
        let hashtag = new Array(1);
        hashtag[0] = "CSCI_571_NewsApp";
        return (
            <div>
                <FacebookShareButton url={this.props.url} hashtag="#CSCI_571_NewsApp">
                    <p data-tip="Facebook" data-for="FacebookTT"><FacebookIcon size={20} round/></p>
                    <ReactTooltip id="FacebookTT"/>
                </FacebookShareButton>
                <TwitterShareButton url={this.props.url} hashtags={hashtag}>
                    <TwitterIcon size={20} round/>
                </TwitterShareButton>
                <EmailShareButton url={this.props.url} subject="#CSCI_571_NewsApp">
                    <EmailIcon size={20} round/>
                </EmailShareButton>
            </div>
        );
    }
}

export default DetailShare;
