import React from "react";
import Navbar from "react-bootstrap/Navbar";
import SelectBar from "../Components/SelectBar";
import Nav from "react-bootstrap/Nav";
import {Link} from "react-router-dom";
import {FaBookmark} from "react-icons/all";
import ReactTooltip from "react-tooltip";

class Favorite extends React.Component {
    constructor(props) {
        super(props);
    }

    componentDidMount() {
        let bookmarks;
        if (window.localStorage.getItem("bookmarks"))
            bookmarks = JSON.parse(window.localStorage.getItem("bookmarks"));
        else
            bookmarks = [];
        if (bookmarks.length === 0) {
            document.getElementById("yes-bookmark").style.display = "none";
        } else
            document.getElementById("no-bookmark").style.display = "none";
    }

    render() {
        return (
            <div>
                <Navbar collapseOnSelect expand="lg" bg="dark" variant="dark" style={{paddingLeft: '0'}}>
                    <SelectBar/>
                    <Navbar.Toggle aria-controls="responsive-navbar"/>
                    <Navbar.Collapse id="responsive-narbar">
                        <Nav className="mr-auto">
                            <Nav.Link as={Link} to="/">Home</Nav.Link>
                            <Nav.Link as={Link} to="/World">World</Nav.Link>
                            <Nav.Link as={Link} to="/Politics">Politics</Nav.Link>
                            <Nav.Link as={Link} to="/Business">Business</Nav.Link>
                            <Nav.Link as={Link} to="/Technology">Technology</Nav.Link>
                            <Nav.Link as={Link} to="/Sports">Sports</Nav.Link>
                        </Nav>
                    </Navbar.Collapse>
                    <p data-tip="Bookmark" data-for="BookmarkTT" data-offset="{'left' : -5, 'right' : -5}"
                       style={{"margin-bottom": 0}}>
                        <FaBookmark color="white" className="mr-2"/>
                    </p>
                    <ReactTooltip
                        id="BookmarkTT"
                        place="bottom"
                        effect="solid"
                        type="dark"/>
                </Navbar>
                <div className="container-fluid">
                    <h4 style={{"margin-left": "0.25rem", "margin-bottom": 0}} id="yes-bookmark">
                        Favorites
                    </h4>
                    <h4 style={{"margin-left": "0.25rem", "margin-bottom": 0, "text-align": "center"}} id="no-bookmark">
                        You have no saved articles
                    </h4>
                </div>
            </div>
        );
    }
}

export default Favorite;
