import React from "react";
import ReactSwitch from "react-switch";

class SwitchBar extends React.Component {
    constructor() {
        super();
        this.state = {checked: false, checkedIcon: false, uncheckedIcon: false, onColor: '#4169E1'};
        this.handleChange = this.handleChange.bind(this);
    }

    handleChange = checked => {
        this.props.changeChecked(checked);
        this.setState({checked});
    }

    render() {
        return (
            <ReactSwitch onChange={this.handleChange} checked={this.state.checked} checkedIcon={this.state.checkedIcon}
                         uncheckedIcon={this.state.uncheckedIcon} onColor={this.state.onColor} className="mr-2"/>
        );
    }
}

export default SwitchBar;
