import React from "react";
import Navbar from "react-bootstrap/Navbar";
import Nav from "react-bootstrap/Nav";
import 'bootstrap/dist/css/bootstrap.min.css';
import SelectBar from "../Components/SelectBar";
import {FaRegBookmark} from "react-icons/all";
import NewsCard from "../Components/NewsCard";
import Row from "react-bootstrap/Row";
import ReactSwitch from "react-switch";

class Home extends React.Component {
    constructor(props) {
        super(props);
        this.state = {SwitchChecked: true, checkedIcon: false, uncheckedIcon: false, onColor: '#4169E1', data: []};
        this.handleChange = this.handleChange.bind(this);
    }

    handleChange = checked => {
        this.setState({SwitchChecked : checked}, () => {
            console.log(this.state.SwitchChecked);
        });
        if(checked){
            this.callGuardian();
        }
        else{
            this.callNYTimes();
        }
    }

    callGuardian() {
            fetch("http://localhost:9000/guardian")
                .then(res => res.json())
                .then(
                    res => {
                        this.setState({data: res['response']['results']});
                    }, error => {
                        this.setState({error});
                    })
        
    }

    callNYTimes() {
            fetch("http://localhost:9000/nytimes")
                .then(res => res.json())
                .then(
                    res => {
                        this.setState({data: res['results']});
                    }, error => {
                        this.setState({error});
                    })
        
    }

    render() {
        return (
            <div>
                <Navbar collapseOnSelect expand="lg" bg="dark" variant="dark" style={{paddingLeft: '0'}}>
                    <SelectBar/>
                    <Navbar.Toggle aria-controls="responsive-navbar"/>
                    <Navbar.Collapse id="responsive-narbar">
                        <Nav className="mr-auto">
                            <Nav.Link href="/">Home</Nav.Link>
                            <Nav.Link href="/World">World</Nav.Link>
                            <Nav.Link href="/Politics">Politics</Nav.Link>
                            <Nav.Link href="/Business">Business</Nav.Link>
                            <Nav.Link href="/Technology">Technology</Nav.Link>
                            <Nav.Link href="/Sports">Sports</Nav.Link>
                        </Nav>
                    </Navbar.Collapse>
                    <FaRegBookmark color="white" className="mr-2"/>
                    <Navbar.Text style={{color: 'white'}} className="mr-2">NYTimes</Navbar.Text>
                    <ReactSwitch 
                        onChange={this.handleChange} 
                        checked={this.state.SwitchChecked} 
                        checkedIcon={this.state.checkedIcon}
                        uncheckedIcon={this.state.uncheckedIcon} 
                        onColor={this.state.onColor} 
                        className="mr-2"/>
                    <Navbar.Text style={{color: 'white'}}>Guardian</Navbar.Text>
                </Navbar>
                <div className="container-fluid">
                    <Row>
                        <p>{this.state.SwitchChecked ? "True" : "False"}</p>
                    </Row>
                    <Row>                      
                        <NewsCard checked={this.state.SwitchChecked} section="" data={this.state.data}/>
                    </Row>
                </div>
            </div>
        );
    }
}

export default Home;
