import React from "react";
import Navbar from "react-bootstrap/Navbar";
import Nav from "react-bootstrap/Nav";
import 'bootstrap/dist/css/bootstrap.min.css';
import SelectBar from "../Components/SelectBar";
import {FaRegBookmark} from "react-icons/all";
import NewsCard from "../Components/NewsCard";
import Row from "react-bootstrap/Row";
import ReactSwitch from "react-switch";
import {Link} from "react-route-dom";

class Politics extends React.Component {
    constructor(props) {
        super(props);
        this.state = {checked: true, checkedIcon: false, uncheckedIcon: false, onColor: '#4169E1'};
        this.handleChange = this.handleChange.bind(this);
    }

    handleChange = checked => {
        this.setState({checked});
        console.log(this.state.checked);
        this.forceUpdate();
    }

    render() {
        return (
            <div>
                <Navbar bg="dark" variant="dark" style={{paddingLeft: '0'}}>
                    <SelectBar/>
                    <Nav className="mr-auto">
                        <Nav.Link as={Link} to="/" href="/">Home</Nav.Link>
                        <Nav.Link as={Link} to="/World" href="/World">World</Nav.Link>
                        <Nav.Link as={Link} to="/Politics" href="/Politics">Politics</Nav.Link>
                        <Nav.Link as={Link} to="/Business" href="/Business">Business</Nav.Link>
                        <Nav.Link as={Link} to="/Technology" href="/Technology">Technology</Nav.Link>
                        <Nav.Link as={Link} to="/Sports" href="/Sports">Sports</Nav.Link>
                    </Nav>
                    <FaRegBookmark color="white" className="mr-2"/>
                    <Navbar.Text style={{color: 'white'}} className="mr-2">NYTimes</Navbar.Text>
                    <ReactSwitch 
                        onChange={this.handleChange} 
                        checked={this.state.checked} 
                        checkedIcon={this.state.checkedIcon}
                        uncheckedIcon={this.state.uncheckedIcon} 
                        onColor={this.state.onColor} 
                        className="mr-2"/>
                    <Navbar.Text style={{color: 'white'}}>Guardian</Navbar.Text>
                </Navbar>
                <div className="container-fluid">
                    <Row>
                        <NewsCard checked={this.state.checked} section="politics"/>
                    </Row>
                </div>
            </div>
        );
    }
}

export default Politics;
