import React, {Component} from "react";
import DetailFavorite from "./DetailFavorite";
import LoadSpinner from "./LoadSpinner";
import Col from "react-bootstrap/Col";

class FavoriteList extends Component {
    constructor(props) {
        super(props);
        this.state = {isLoaded: false, datas: []};
    }

    callGuardian = (id, idx) => {
        fetch("http://localhost:9000/guardian/article" + id)
            .then(res => res.json())
            .then(
                res => {
                    let newdata = this.state.datas;
                    newdata.push(res['response']['content']);
                    this.setState({datas: newdata});
                    if (idx === this.props.ids.length - 1)
                        this.setState({isLoaded: true});
                }, error => {
                    this.setState({error});
                })
    };

    callNYTimes = (id, idx) => {
        fetch("http://localhost:9000/nytimes/article" + id)
            .then(res => res.json())
            .then(
                res => {
                    let newdata = this.state.datas;
                    newdata.push(res['response']['docs'][0]);
                    this.setState({datas: newdata});
                    if (idx === this.props.ids.length - 1)
                        this.setState({isLoaded: true});
                }, error => {
                    this.setState({error});
                })
    };

    componentDidMount() {
        let id = [];
        for (let i = 0; i < this.props.ids.length; i++) {
            id.push(this.props.ids[i]);
        }
        for (let i = 0; i < id.length; i++) {
            let checking = id[i].substr(4, 4) !== 'http';
            if (checking)
                this.callGuardian(id[i], i);
            else
                this.callNYTimes(id[i], i);
        }
    }

    render() {
        if (this.props.ids.length === 0) {
            return null;
        } else if (this.state.error) {
            return <div>Error: {this.state.error.message}</div>;
        } else if (!this.state.isLoaded) {
            return <LoadSpinner/>;
        } else {
            let array = [];
            for (let i = 0; i < this.state.datas.length; i++) {
                array.push(
                    <Col md={3} style={{"padding": 0}}>
                        <DetailFavorite key={i} data={this.state.datas[i]}/>
                    </Col>
                );
            }
            return (
                <div style={{"display": "flex", "flex-wrap": "wrap"}}>
                    {array}
                </div>
            );
        }
    };
}

export default FavoriteList;
