import React from "react";
import Navbar from "react-bootstrap/Navbar";
import Nav from "react-bootstrap/Nav";
import 'bootstrap/dist/css/bootstrap.min.css';
import SelectBar from "../Components/SelectBar";
import {FaRegBookmark} from "react-icons/all";
import CardList from "../Components/CardList";
import Row from "react-bootstrap/Row";
import ReactSwitch from "react-switch";
import {Link, NavLink} from 'react-router-dom';
import ReactTooltip from "react-tooltip";

class Home extends React.Component {
    constructor(props) {
        super(props);
        this.state = {
            SwitchChecked: props.checked,
            checkedIcon: false,
            uncheckedIcon: false,
            onColor: '#49f',
            offColor: '#eee',
            data: [],
            isLoaded: false
        };
        this.handleChange = this.handleChange.bind(this);
    }

    static getDerivedStateFromProps(nextProps, nextState) {
        if (nextProps.checked !== nextState.SwitchChecked) {
            return {
                SwitchChecked: nextProps.checked
            };
        }
        return null;
    }

    handleChange = checked => {
        window.localStorage.setItem("switchchecked", checked);
        this.setState({SwitchChecked: checked});
        this.setState({isLoaded: true});
        if (checked) {
            this.callGuardian();
        } else {
            this.callNYTimes();
        }
    };

    callGuardian() {
        fetch("https://yli59322-hw8-571-backend.appspot.com/guardian")
            .then(res => res.json())
            .then(
                res => {
                    this.setState({data: res});
                }, error => {
                    this.setState({error});
                })
            .then(() => this.setState({isLoaded: true}))

    }

    callNYTimes() {
        fetch("https://yli59322-hw8-571-backend.appspot.com/nytimes")
            .then(res => res.json())
            .then(
                res => {
                    this.setState({data: res});
                }, error => {
                    this.setState({error});
                })
            .then(() => this.setState({isLoaded: true}))

    }

    render() {
        return (
            <div className="container-fluid">
                <Row>
                    <CardList checked={this.state.SwitchChecked} section="" data={this.state.data}/>
                </Row>
            </div>
        );
    }
}

export default Home;
