import React from "react";
import Card from "react-bootstrap/Card";

class NewsCard extends React.Component {
    constructor(props) {
        super(props);
        this.state = {error: null, isLoaded: false, data: []};
    }

    callAPI(url) {
        fetch(url)
            .then(res => res.json())
            .then(res => this.setState({isLoaded:true,data:res['response']['results']}))
    }

    componentDidMount() {
        this.callAPI("http://localhost:9000/guardian");
    }

    render() {
        return (
            <Card>
                <Card.Body>
                    <Card.Title>{this.state.apiResponse[0]['webTitle']}</Card.Title>
                    <Card.Text>Text</Card.Text>
                    <Card.Footer>Footer</Card.Footer>
                </Card.Body>
            </Card>
        );
    }
}

export default NewsCard;
